clean_movie_dict = {}

for movie in movie_dict.values():
    for sub_movie in range(0,len(movie)):

        if movie[sub_movie]['is_new'] == True:
            clean_movie_dict[(movie)[sub_movie]['id']] = movie[sub_movie]
            
        elif movie[sub_movie]['is_new'] == False:
            if float(movie[sub_movie]['rate']) >= 8:
                clean_movie_dict[(movie)[sub_movie]['id']] = movie[sub_movie]

